package day3;

public class Assignment_operator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=20;
	    a+=5;    //a=a+5;
		System.out.println(a);

		a-=5; //a=a-5;
		System.out.println(a);
		int b=10;int c=30;int d=10;
		b*=2;  //a=a*5
		c%=3;  //a=a%5
		d/=2;  //a=a/5
		System.out.println(b+" "+d+" "+c);
		
	}

}
